import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ProductList() {
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);

  useEffect(() => {
    // Dohvaćanje svih proizvoda s API-ja
    axios.get('http://localhost:8080/products')
      .then(response => {
        setProducts(response.data);
      })
      .catch(error => {
        console.error(error);
      });

    // Dohvaćanje svih kategorija proizvoda s API-ja
    axios.get('http://localhost:8080/productscategory')
      .then(response => {
        setCategories(response.data);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  // Kreiranje objekta s praznim poljima za svaku kategoriju
  const categorizedProducts = products.reduce((categories, product) => {
    const category = product.category;
    if (!categories[category]) {
      categories[category] = [];
    }
    categories[category].push(product);
    return categories;
  }, {});

  return (
    <div>
      <nav>
        <ul>
          <li>
            <a href="#all">All</a>
          </li>
          {/* Prikazivanje navigacije za kategorije */}
          {categories.map(category => (
            <li key={category.id}>
              <a href={`#${category.name}`}>{category.name}</a>
            </li>
          ))}
        </ul>
      </nav>

      <h1>Product List</h1>

      {/* Prikazivanje svih proizvoda */}
      <div id="all">
        <h2>All Products</h2>
        <ul>
          {products.map(product => (
            <li key={product.id}>{product.name}</li>
          ))}
        </ul>
      </div>

      {/* Prikazivanje kategorija i proizvoda unutar kategorija */}
      {Object.entries(categorizedProducts).map(([category, productList]) => (
        <div key={category} id={category}>
          <h2>{category}</h2>
          <ul>
            {productList.map(product => (
              <li key={product.id}>{product.name}</li>
            ))}
          </ul>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
