import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './ProductList.css';
import authHeader from './services/auth-header';

function ProductList() {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [productCategories, setProductCategories] = useState([]);
    const [colors, setColors] = useState([]);
    const [sizes, setSizes] = useState([]);
    const [genders, setGenders] = useState([]);
    const [orderId, setOrderId] = useState(localStorage.getItem('orderId'));
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [formFields, setFormFields] = useState({
        name: '',
        description: '',
        price: '',
        color: '',
        size: '',
        gender: '',
        imageURL: ''
    });

    useEffect(() => {
        fetchProducts();
        fetchProductCategories();
        fetchColors();
        fetchSizes();
        fetchGenders();
    }, []);

    useEffect(() => {
        if (selectedCategory) {
            fetchProductsByCategory(selectedCategory);
        } else {
            fetchProducts();
        }
    }, [selectedCategory]);

    useEffect(() => {
        console.log("isModalOpen changed:", isModalOpen);
    }, [isModalOpen]);

    const fetchProducts = async () => {
        const response = await axios.get('http://localhost:8080/products', { headers: authHeader() });
        console.log("Fetched products:", response.data);
        setProducts(response.data);
    };

    const fetchProductsByCategory = async (category) => {
        const response = await axios.get(`http://localhost:8080/products?category=${category}`, {
            headers: authHeader(),
        });
        setProducts(response.data);
    };

    const fetchProductCategories = async () => {
        const response = await axios.get('http://localhost:8080/productscategory', {
            headers: authHeader(),
        });
        setProductCategories(response.data);
    };

    const fetchColors = async () => {
        const response = await axios.get('http://localhost:8080/colors', {
            headers: authHeader(),
        });
        setColors(response.data);
    };

    const fetchSizes = async () => {
        const response = await axios.get('http://localhost:8080/sizes', {
            headers: authHeader(),
        });
        setSizes(response.data);
    };

    const fetchGenders = async () => {
        const response = await axios.get('http://localhost:8080/genders', {
            headers: authHeader(),
        });
        setGenders(response.data);
    };

    const createOrder = async () => {
        const response = await axios.post('http://localhost:8080/orders', {}, { headers: authHeader() });
        setOrderId(response.data.id);
        return response.data.id;
    };

    const addToOrder = async (productId, price, quantity = 1) => {
        let currentOrderId = orderId;
        if (!currentOrderId) {
            currentOrderId = await createOrder();  
        }
        const data = {
            quantity: quantity,
            price: price,
        };
        await axios.post(`http://localhost:8080/orderDetails?orderId=${currentOrderId}&productId=${productId}`, data, {
            headers: authHeader(),
        });
        alert(`Proizvod s ID-om ${productId} dodan u narudžbu.`);
    };

    const deleteProduct = async (productId) => {
        await axios.delete(`http://localhost:8080/products/${productId}`, {
            headers: authHeader(),
        });
        alert('Proizvod uspješno obrisan.');
        fetchProducts();
    };

    const openModal = (product) => {
        console.log("Opening modal for product:", product);
        setEditingProduct(product);
        setFormFields({
            name: product.name,
            description: product.description,
            price: product.price,
            color: product.color.id,
            size: product.size.id,
            gender: product.gender.id,
            imageURL: product.imageURL
        });
        setModalOpen(true);
    };

    const closeModal = () => {
        setModalOpen(false);
        setEditingProduct(null);
        fetchProducts();
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormFields(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    const handleEditSubmit = async (event) => {
        event.preventDefault();
        console.log('Form fields before update:', formFields);
        const updatedProduct = {
            ...editingProduct,
            name: formFields.name,
            description: formFields.description,
            price: formFields.price,
            color: { id: formFields.color },
            size: { id: formFields.size },
            gender: { id: formFields.gender },
            imageURL: formFields.imageURL
        };
        console.log("Updated product:", updatedProduct);
        await axios.put(`http://localhost:8080/products/${editingProduct.id}`, updatedProduct, {
            headers: authHeader(),
        });
        closeModal();
    };

    return (
        <div>
            <h2>Popis proizvoda</h2>
            <div className="product-list">
                {products.map((product) => (
                    <div key={product.id} className="product-item">
                        <h3>{product.name}</h3>
                        <img 
                            src={`http://localhost:8080/products/images/${product.imageURL}`}  
                            alt="Slika proizvoda"
                            className="product-image"
                        />
                        <p>{product.description}</p>
                        <p>Veličina: {sizes.find(size => size.id === product.size.id)?.name}</p>
                        <p>Cijena: {product.price}€</p>
                        <p>Boja: {colors.find(color => color.id === product.color.id)?.name}</p>
                        <p>Spol: {genders.find(gender => gender.id === product.gender.id)?.name}</p>
                        <button onClick={() => addToOrder(product.id, product.price)}>Dodaj u košaricu</button>
                        <button onClick={() => openModal(product)}>Uredi</button>
                        <button onClick={() => deleteProduct(product.id)}>Obriši</button>

                        {isModalOpen && editingProduct === product && 
                            <div className="edit-modal">
                                <form onSubmit={handleEditSubmit}>
                                    <input type="text" name="name" value={formFields.name} onChange={handleInputChange} placeholder="Ime proizvoda" />
                                    <textarea name="description" value={formFields.description} onChange={handleInputChange} placeholder="Opis"></textarea>
                                    <input type="number" name="price" value={formFields.price} onChange={handleInputChange} placeholder="Cijena" />
                                    <div>
                                        <label>Boja:</label>
                                        <select name="color" value={formFields.color} onChange={handleInputChange}>
                                            {colors.map(color => <option key={color.id} value={color.id}>{color.name}</option>)}
                                        </select>
                                    </div>
                                    <div>
                                        <label>Veličina:</label>
                                        <select name="size" value={formFields.size} onChange={handleInputChange}>
                                            {sizes.map(size => <option key={size.id} value={size.id}>{size.name}</option>)}
                                        </select>
                                    </div>
                                    <div>
                                        <label>Spol:</label>
                                        <select name="gender" value={formFields.gender} onChange={handleInputChange}>
                                            {genders.map(gender => <option key={gender.id} value={gender.id}>{gender.name}</option>)}
                                        </select>
                                    </div>
                                    <input type="text" name="imageURL" value={formFields.imageURL} onChange={handleInputChange} placeholder="URL Slike" />
                                    <button type="submit">Ažuriraj</button>
                                    <button onClick={closeModal}>Odustani</button>
                                </form>
                            </div>
                        }
                    </div>
                ))}
            </div>
            <Link to="/cart">Prikaži košaricu</Link>
        </div>
    );
}

export default ProductList;
