import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';
import axios from 'axios';



const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const loginUser =(email, password) => {
    const [redirectTo, setRedirectTo] = useState(null);
    const user={
      email: email,
      password: password,
    } 
    try {
      const response = axios.post('http://localhost:8080/users/login', user)
      console.log('Odgovor:', response)
      setRedirectTo('/login');
  
  
    } catch (error) {
      console.error('Greška:', error.response.data);
    }
  
  };
  if (redirectTo) {
    return <Redirect to={redirectTo} />;
  }
  

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    loginUser(email, password);
  };

  return (
    <div>
      <h2>Login</h2>
      E-mail:<input type="text" name='email' placeholder="Email" value={email} onChange={handleEmailChange} /><br></br>
      Password:<input type="password" name='password' placeholder="Password" value={password} onChange={handlePasswordChange} />
      <button onClick={handleLogin}>Login</button>
    </div>
  );
};

export default LoginForm;
