import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './OrderList.css';
import authHeader from './services/auth-header';

class AllOrders extends Component {
    state = {
        orders: []
    }

    componentDidMount() {
        axios.get('http://localhost:8080/orderDetails/all') 
            .then(response => {
                this.setState({ orders: response.data });
            })
            .catch(error => {
                console.error('There was an error!', error);
            });
    }

    render() {
        return (
            <div>
                <h2>Sve Narudžbe</h2>
                <table className="table">
                    <thead>
                        <tr>
                            <th>ID narudžbe</th>
                            <th>Proizvod</th>
                            <th>Količina</th>
                            <th>Cijena</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.state.orders.map(order => (
                            <tr key={order.id}>
                                <td>{order.orderId}</td>
                                <td>{order.product.name}</td> {/* Pretpostavka da proizvod ima ime */}
                                <td>{order.quantity}</td> {/* Pretpostavka da narudžba ima količinu */}
                                <td>{order.price}</td> {/* Pretpostavka da narudžba ima cijenu */}
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        );
    }
}

export default AllOrders;
