import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import placeholderImage from './images/placeholder-image.png';
import './ProductList.css';

function ProductList({ onAddToOrder }) {
  // State variables
  const [products, setProducts] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState('');
  const [productCategories, setProductCategories] = useState([]);

  // Fetch products and product categories on component mount
  useEffect(() => {
    fetchProducts();
    fetchProductCategories();
  }, []);

  // Fetch products based on selected category
  useEffect(() => {
    if (selectedCategory) {
      fetchProductsByCategory(selectedCategory);
    } else {
      fetchProducts();
    }
  }, [selectedCategory]);

  // Fetch all products
  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8080/products');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja proizvoda.');
    }
  };

  // Fetch products by category
  const fetchProductsByCategory = async (category) => {
    try {
      const response = await axios.get(`http://localhost:8080/products?category=${category}`);
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja proizvoda.');
    }
  };

  // Fetch all product categories
  const fetchProductCategories = async () => {
    try {
      const response = await axios.get('http://localhost:8080/productscategory');
      setProductCategories(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja kategorija proizvoda.');
    }
  };

// Add a product to the order
const addToOrder = (productId, quantity, orderId) => {
  const data = {
      productId: productId,
      quantity: quantity,
      orderId: orderId,
  };

  axios
      .post(`http://localhost:8080/orderDetails?orderId=${orderId}`, data)
      .then((response) => {
          alert(`Proizvod s ID-om ${productId} dodan u narudžbu.`);
      })
      .catch((error) => {
        console.error('Došlo je do pogreške prilikom dodavanja proizvoda u narudžbu:', error);
    });
};
{products.map((product) => {
  // State for the quantity of the current product
  const [quantity, setQuantity] = useState(1);

  return (
    <div key={product.id} className="product-item">
      {/* Rest of the product information */}
      <p>Veličina: {product.size?.name}</p>
      <p>Cijena: {product.price}€</p>
      <p>Boja: {product.color?.name}</p>
      <p>Spol: {product.gender?.name}</p>

      {/* Quantity input */}
      <input type="number" min="1" value={quantity} onChange={(e) => setQuantity(e.target.value)} />

      {/* Updated addToOrder call */}
      <button onClick={() => addToOrder(product.id, quantity)}>Dodaj u narudžbu</button>
    </div>
  );
})}
}

export default ProductList;
