import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import '../ProductList.css';
import authHeader from '../services/auth-header';

function ProductList() {
    const [products, setProducts] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState('');
    const [productCategories, setProductCategories] = useState([]);
    const [colors, setColors] = useState([]);
    const [sizes, setSizes] = useState([]);
    const [genders, setGenders] = useState([]);
    const [orderId, setOrderId] = useState(localStorage.getItem('orderId'));
    const [isModalOpen, setModalOpen] = useState(false);
    const [editingProduct, setEditingProduct] = useState(null);
    const [isAdmin, setIsAdmin] = useState(false);
    const [sortType, setSortType] = useState('none');
    const [formFields, setFormFields] = useState({
        name: '',
        description: '',
        price: '',
        color: '',
        size: '',
        gender: '',
        imageURL: ''
    });
    const [currentPage, setCurrentPage] = useState(1);
    const [productsPerPage] = useState(10);

    useEffect(() => {
        fetchProducts();
        fetchProductCategories();
        fetchColors();
        fetchSizes();
        fetchGenders();
    }, []);

    useEffect(() => {
        if (selectedCategory) {
            fetchProductsByCategory(selectedCategory);
        } else {
            fetchProducts();
        }
    }, [selectedCategory]);

    const fetchProducts = async () => {
        const response = await axios.get('http://localhost:8080/products', {
            headers: authHeader(),
        });
        setProducts(response.data);
    };

    const fetchProductsByCategory = async (category) => {
        const response = await axios.get(`http://localhost:8080/products?category=${category}`, {
            headers: authHeader(),
        });
        setProducts(response.data);
    };

    const fetchProductCategories = async () => {
        const response = await axios.get('http://localhost:8080/categories', {
            headers: authHeader(),
        });
        setProductCategories(response.data);
    };

    const fetchColors = async () => {
        const response = await axios.get('http://localhost:8080/colors', {
            headers: authHeader(),
        });
        setColors(response.data);
    };

    const fetchSizes = async () => {
        const response = await axios.get('http://localhost:8080/sizes', {
            headers: authHeader(),
        });
        setSizes(response.data);
    };

    const fetchGenders = async () => {
        const response = await axios.get('http://localhost:8080/genders', {
            headers: authHeader(),
        });
        setGenders(response.data);
    };

    const handlePageChange = (page) => {
        setCurrentPage(page);
    };

    const indexOfLastProduct = currentPage * productsPerPage;
    const indexOfFirstProduct = indexOfLastProduct - productsPerPage;
    const currentProducts = products.slice(indexOfFirstProduct, indexOfLastProduct);

    return (
        <div>
            <h2>Product List</h2>
            <div className='filter-section'>
                <div className="sort-options">
                    <label htmlFor='sortType'>Sort Products: </label>
                    <select id='sortType' value={sortType} onChange={(e) => setSortType(e.target.value)}>
                        <option value='none'>No Sorting</option>
                        <option value='asc'>Price Low to High</option>
                        <option value='desc'>Price High to Low</option>
                    </select>
                </div>
            </div>
            <div className="category-list">
                <button onClick={() => setSelectedCategory('')}>All Products</button>
                {productCategories.map((category) => (
                    <button key={category.id} onClick={() => setSelectedCategory(category.name)}>
                        {category.name}
                    </button>
                ))}
            </div>
            <div className="product-list">
                {currentProducts.map((product) => (
                    <div key={product.id} className="product-item">
                        <div className="product-image-container">
                            <Link to={`/product/${product.id}`}>
                                <img
                                    src={`http://localhost:8080/products/images/${product.imageURL}`}
                                    alt="product image"
                                    className="product-image"
                                />
                            </Link>
                        </div>
                        <h3>{product.name}</h3>
                        <p>{product.price} €</p>
                    </div>
                ))}
            </div>
            <div className="pagination">
                {[...Array(Math.ceil(products.length / productsPerPage)).keys()].map((page, index) => (
                    <button key={index} onClick={() => handlePageChange(page + 1)}>
                        {page + 1}
                    </button>
                ))}
            </div>
            <Link to="/cart">View Cart</Link>
        </div>
    );
}

export default ProductList;
