import React, { useState } from 'react';
import './AddProductCategory.css'; // Import the CSS file for styling

function AddProductCategory() {
  const [name, setName] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleNameChange = e => {
    setName(e.target.value);
  };

  const handleImageUrlChange = e => {
    setImageUrl(e.target.value);
  };

  const handleSubmit = e => {
    e.preventDefault();

    // Create the product category object
    const productCategory = {
      name: name,
      imageUrl: imageUrl
    };

    // Make a POST request to the backend API to save the product category
    fetch('http://localhost:8080/productscategory', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(productCategory)
    })
      .then(response => response.json())
      .then(data => {
        // Handle the response from the server
        console.log(data); // You can perform additional actions here if needed
      })
      .catch(error => {
        console.error('Error:', error);
      });

    // Reset the form fields
    setName('');
    setImageUrl('');
  };

  return (
    <div className="add-product-category">
      <h1>Add Product Category</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label>Image URL:</label>
          <input type="text" value={imageUrl} onChange={handleImageUrlChange} />
        </div>
        <button type="submit">Add Product Category</button>
      </form>
    </div>
  );
}

export default AddProductCategory;
