import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import '../AllOrders.css';

function AllOrders() {
    const [users, setUsers] = useState([]);
    const [selectedUser, setSelectedUser] = useState(null);
    const [orders, setOrders] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [topSpenders, setTopSpenders] = useState([]);
    const [topBuyers, setTopBuyers] = useState([]);
    const [purchased, setPurchased] = useState([]);
    const [groupedOrders, setGroupedOrders] = useState({});
    const [selectedOrderId, setSelectedOrderId] = useState(null);

    useEffect(() => {
        fetchAllUsers();
        fetchTopSpenders();
        fetchTopBuyers();
        fetchPurchased();
    }, []);

    const fetchAllUsers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/users', {
                headers: authHeader(),
            });
            setUsers(response.data);
        } catch (error) {
            console.error('Error fetching users:', error);
        }
    };

    const fetchOrdersByUser = async (userId) => {
        try {
            const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${userId}`, {
                headers: authHeader(),
            });
            setOrders(response.data);

            const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
            setTotalPrice(total);
        } catch (error) {
            console.error('Error fetching user orders:', error);
        }
    };

    const fetchTopSpenders = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/top-spending-users', {
                headers: authHeader(),
            });
            setTopSpenders(response.data);
        } catch (error) {
            console.error('Error fetching top spenders:', error);
        }
    };

    const fetchPurchased = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/products-by-day', {
                headers: authHeader(),
            });
            setPurchased(response.data);
        } catch (error) {
            console.error('Error fetching purchased:', error);
        }
    };

    const fetchTopBuyers = async () => {
        try {
            const response = await axios.get('http://localhost:8080/orderDetails/top-selling-products', {
                headers: authHeader(),
            });
            setTopBuyers(response.data);
        } catch (error) {
            console.error('Error fetching top buyers:', error);
        }
    };

    const handleUserClick = async (user) => {
        setSelectedUser(user);
        setOrders([]);
        setTotalPrice(0);
        await fetchOrdersByUser(user.id);

        let tempGroupedOrders = {};
        orders.forEach(orderDetail => {
            const orderId = orderDetail.order.id;
            if (!tempGroupedOrders[orderId]) {
                tempGroupedOrders[orderId] = [];
            }
            tempGroupedOrders[orderId].push(orderDetail);
        });
        setGroupedOrders(tempGroupedOrders);
    };

    const handleOrderClick = (orderId) => {
        setSelectedOrderId(orderId === selectedOrderId ? null : orderId);
    };

    return (
        <div className="orders-container">
            <h2>Users</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>
                                <a href="#" onClick={() => handleUserClick(user)}>{user.username}</a>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Top Spenders</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Username</th>
                        <th>Email</th>
                    </tr>
                </thead>
                <tbody>
                    {topSpenders.map((user) => (
                        <tr key={user.id}>
                            <td>{user.username}</td>
                            <td>{user.email}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Best selling products</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Products</th>
                        <th>Images</th>
                    </tr>
                </thead>
                <tbody>
                    {topBuyers.map((product) => (
                        <tr key={product.id}>
                            <td>{product.name}</td>
                            <td>
                                <img src={`http://localhost:8080/products/images/${product.imageURL}`} alt={product.name} width="100" />
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <h2>Purchased by day</h2>
            <table className="users-table">
                <thead>
                    <tr>
                        <th>Product</th>
                        <th>Images</th>
                        <th>Quantity</th>
                        <th>Date</th>
                    </tr>
                </thead>
                <tbody>
                    {purchased.map((item, index) => (
                        <tr key={index}>
                            <td>{item.product.name}</td>
                            <td>
                                <img src={`http://localhost:8080/products/images/${item.product.imageURL}`} alt={item.product.name} width="100" />
                            </td>
                            <td>{item.quantity}</td>
                            <td>{new Date(item.date).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {selectedUser && (
                <div>
                    <h3>Orders of {selectedUser.username}</h3>
                    {Object.keys(groupedOrders).length === 0 ? (
                        <p>This user hasn't placed any orders yet.</p>
                    ) : (
                        Object.keys(groupedOrders).map((orderId) => (
                            <div key={orderId}>
                                <h3 onClick={() => handleOrderClick(orderId)}>Order ID: {orderId}</h3>
                                {selectedOrderId === orderId && (
                                    <table className="orders-table">
                                        <thead>
                                            <tr>
                                                <th>Product Image</th>
                                                <th>Product Name</th>
                                                <th>Quantity</th>
                                                <th>Price</th>
                                                <th>Date of Order</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                            {groupedOrders[orderId].map((orderDetail, index) => (
                                                <tr key={index}>
                                                    <td><img src={`http://localhost:8080/products/images/${orderDetail.product.imageURL}`} alt={orderDetail.product.name} width="100" /></td>
                                                    <td>{orderDetail.product.name}</td>
                                                    <td>{orderDetail.quantity}</td>
                                                    <td>{orderDetail.product.price} â‚¬</td>
                                                    <td>{new Date(orderDetail.time).toLocaleString()}</td>
                                                </tr>
                                            ))}
                                        </tbody>
                                    </table>
                                )}
                            </div>
                        ))
                    )}
                </div>
            )}
        </div>
    );
}

export default AllOrders;
