import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import AuthService from '../services/auth.service';
import '../AllOrders.css';

function OrderDetailsPage() {
    const [orderDetails, setOrderDetails] = useState([]);  // Changed from 'orders' to 'orderDetails' for clarity
    const [totalPrice, setTotalPrice] = useState(0);
    const [orderId, setOrderId] = useState(180);  // Assuming you want to start with orderId 180

    useEffect(() => {
        fetchOrderDetails(orderId);
    }, [orderId]);  // Dependency array to re-run useEffect when orderId changes

    const fetchOrderDetails = async (id) => {
        try {
            const response = await axios.get(`http://localhost:8080/orderDetails/${id}`, {
                headers: authHeader(),
            });
            setOrderDetails(response.data);
            const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
            setTotalPrice(total);
        } catch (error) {
            console.error('Error fetching order details:', error);
        }
    };

    // Function to set the order ID based on user's click (Assuming orderId comes from the click event)
    const handleOrderClick = (id) => {
        setOrderId(id);
    };

    return (
        <div className="orders-container">
            <h2>My Orders</h2>
            {orderDetails.length === 0 ? (
                <p>You haven't placed any orders yet.</p>
            ) : (
                <table className="orders-table">
                    <thead>
                        <tr>
                            <th>Product Image</th>
                            <th>Product Name</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>Date of Order</th>
                        </tr>
                    </thead>
                    <tbody>
                        {orderDetails.map((orderDetail) => (
                            <tr key={orderDetail.id}>
                                <td><img src={`http://localhost:8080/products/images/${orderDetail.product.imageURL}`} alt={orderDetail.product.name} width="100" /></td>
                                <td>{orderDetail.product.name}</td>
                                <td>{orderDetail.quantity}</td>
                                <td>{orderDetail.product.price} €</td>
                                <td>{new Date(orderDetail.orderTime).toLocaleString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            )}
            <strong><p>Total Price: {totalPrice} €</p></strong>
            {/* Add some UI component to set the order ID based on user's click. This is just an example button. */}
            <button onClick={() => handleOrderClick(181)}>Show Order 181</button>
        </div>
    );
}

export default OrderDetailsPage;
