import React, { useEffect, useState } from 'react';
import axios from 'axios';
import AuthService from "../services/auth.service";
import authHeader from '../services/auth-header';  // Pretpostavljam da imate ovu funkciju za generisanje headera

const UserOrders = () => {
  const [orders, setOrders] = useState([]);
  const [totalPrice, setTotalPrice] = useState(0);

  const fetchOrdersByUser = async (userId) => {
    try {
      const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${userId}`, {
        headers: authHeader(),
      });
      setOrders(response.data);

      const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
      setTotalPrice(total);
    } catch (error) {
      console.error('Error fetching user orders:', error);
    }
  };

  useEffect(() => {
    const user = AuthService.getCurrentUser();
    if (user) {
      fetchOrdersByUser(user.id);
    }
  }, []);

  return (
    <div>
      <h2>Your Past Orders</h2>
      <p>Total Price: ${totalPrice.toFixed(2)}</p>
      {orders.length === 0 ? (
        <p>You have no past orders.</p>
      ) : (
        <ul>
          {orders.map((order, index) => (
            <li key={index}>
              <div>
                Order ID: {order.id} <br />
                Product: {order.productName} <br />
                Quantity: {order.quantity} <br />
                Date: {new Date(order.dateOrdered).toLocaleDateString()}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default UserOrders;
