import React, { useState } from 'react';
import axios from 'axios';
import './loginForm.css'


const logoutUser = () => {
    // Obrišite token iz localStorage-a
    localStorage.removeItem('token');
  
    window.location.href = '/login';
  };
  

const LoginForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  useEffect(() => {
    axios.interceptors.request.use((config) => {
      const token = localStorage.getItem('token');
      if (token) {
          config.headers['Authorization'] = 'Bearer ' + token;
      }
      return config;
    }, (error) => {
        return Promise.reject(error);
    });
  }, []);


  const loginUser = async (email, password) => {
    const user = {
      email: email,
      password: password,
    };
  
    try {
      const response = await axios.post('http://localhost:8080/login', user);
      console.log('Odgovor:', response);
      // Spremite token u localStorage, 
      // ali ovo će trenutno biti undefined jer server ne šalje token
      localStorage.setItem('token', response.data.token);
  
      if(response.status === 200) {
        window.location.href = '/home';
      }
    } catch (error) {
      console.error(error);
  
      if (error.response) {
        // Ako postoji odgovor od servera, prikažite poruku o grešci
        setErrorMessage(error.response.data);
      } else {
        // Ako ne postoji odgovor (npr. mrežna greška), postavite generičku poruku o grešci
        setErrorMessage("Došlo je do greške prilikom prijave.");
      }
    }
  };

  const handleEmailChange = (event) => {
    setEmail(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleLogin = () => {
    loginUser(email, password);
  };

  return (
    <div className="login-container">
      <h1 className='loginfont'>Login</h1>
      <ul>
      {errorMessage && <p>{errorMessage}</p>}
        <li>E-mail:<input className="login-input" type="text" name='email' placeholder="Email" value={email} onChange={handleEmailChange} /></li>
        <li>Password:<input className="login-input"  type="password" name='password' placeholder="Password" value={password} onChange={handlePasswordChange} /></li>
        <button className="login-button" onClick={handleLogin}>Login</button>
      </ul>
    </div>
  );
};

export default LoginForm;
