import React, { useState, useEffect } from 'react';
import axios from 'axios';
import placeholderImage from './images/placeholder-image.png';
import './ProductList.css'; // Import the CSS file for styling

function ProductList() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  }, []);

  const fetchProducts = async () => {
    try {
      const response = await axios.get('http://localhost:8080/products');
      setProducts(response.data);
    } catch (error) {
      console.error(error);
      alert('Došlo je do pogreške prilikom dohvaćanja proizvoda.');
    }
  };

  const handleOrder = (productId) => {
    // Ovdje rješavate logiku narudžbe
    alert(`Naruči proizvod s ID-om ${productId}`);
  };

  return (
    <div>
      <h2>Popis proizvoda</h2>
      {products.map((product) => (
        <div key={product.id} className="product-item">
          <h3>{product.name}</h3>
          <img
            src={product.imageURL ? `http://localhost:8080${product.imageURL}` : placeholderImage}
            alt={product.name}
            className="product-image"
          />
          <p>{product.description}</p>
          <p>Veličina: {product.size.name}</p>
          <p>Cijena: {product.price}</p>
          <p>Boja: {product.color.name}</p>
          <p>Spol: {product.gender.name}</p>
          <p>Kategorija: {product.category.name}</p>
          <button onClick={() => handleOrder(product.id)}>Naruči</button>
        </div>
      ))}
    </div>
  );
}

export default ProductList;
