import React, { useState } from 'react';
import './AddProductCategory.css'; // Import the CSS file for styling

function AddProductCategory() {
  const [name, setName] = useState('');
  const [image, setImage] = useState(null);

  const handleNameChange = e => {
    setName(e.target.value);
  };

  const handleImageChange = e => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleSubmit = e => {
    e.preventDefault();

    // Create a FormData object
    const formData = new FormData();
    formData.append('name', name);
    formData.append('image', image);

    // Make a POST request to the backend API to save the product category
    fetch('http://localhost:8080/product-categories', {
      method: 'POST',
      body: formData
    })
      .then(response => response.json())
      .then(data => {
        // Handle the response from the server
        console.log(data); // You can perform additional actions here if needed
      })
      .catch(error => {
        console.error('Error:', error);
      });

    // Reset the form fields
    setName('');
    setImage(null);
  };

  return (
    <div className="add-product-category">
      <h1>Add Product Category</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={handleNameChange} />
        </div>
        <div>
          <label>Image:</label>
          <input type="file" accept="image/*" onChange={handleImageChange} />
        </div>
        <button type="submit">Add Product Category</button>
      </form>
    </div>
  );
}

export default AddProductCategory;
