import React, { useState, useEffect } from 'react';
import axios from 'axios';
import authHeader from '../services/auth-header';
import AuthService from '../services/auth.service';
import { Link } from 'react-router-dom';
import '../AllOrders.css';

function MyOrders() {
    const [orders, setOrders] = useState([]);
    const [totalPrice, setTotalPrice] = useState(0);
    const [currentPage, setCurrentPage] = useState(1);
    const [ordersPerPage] = useState(5);

    useEffect(() => {
        fetchMyOrders();
    }, []);

    const fetchMyOrders = async () => {
        const user = AuthService.getCurrentUser();
        
        if (user) {
            try {
                const response = await axios.get(`http://localhost:8080/orderDetails/details/user/${user.id}`, {
                    headers: authHeader(),
                });
                setOrders(response.data);

                const total = response.data.reduce((acc, orderDetail) => acc + (orderDetail.quantity * orderDetail.product.price), 0);
                setTotalPrice(total);
            } catch (error) {
                console.error('Error fetching my orders:', error);
            }
        }
    };

    const indexOfLastOrder = currentPage * ordersPerPage;
    const indexOfFirstOrder = indexOfLastOrder - ordersPerPage;
    const currentOrders = orders.slice(indexOfFirstOrder, indexOfLastOrder);
    const totalPages = Math.ceil(orders.length / ordersPerPage);

    return (
        <div className="orders-container">
            <h2>My Orders</h2>
            {currentOrders.length === 0 ? (
                <p>You haven't placed any orders yet.</p>
            ) : (
                <>
                <table className="orders-table">
                    <thead>
                        <tr>
                            <th>Product Image</th>
                            <th>Product Name</th>
                            <th>Quantity</th>
                            <th>Price</th>
                            <th>Date of Order</th>
                        </tr>
                    </thead>
                    <tbody>
                        {currentOrders.map((orderDetail) => (
                            <tr key={orderDetail.id}>
                                <td><img src={`http://localhost:8080/products/images/${orderDetail.product.imageURL}`} alt={orderDetail.product.name} width="100" /></td>
                                <td><Link to={`/orderDetails/${orderDetail.id}`}>{orderDetail.product.name}</Link></td>
                                <td>{orderDetail.quantity}</td>
                                <td>{orderDetail.product.price} €</td>
                                <td>{new Date(orderDetail.orderTime).toLocaleString()}</td>
                            </tr>
                        ))}
                    </tbody>
                </table>
                <strong><p>Total Price: {totalPrice} €</p></strong>
                <div>
                    <button onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage <= 1}>Prethodna</button>
                    <button onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage >= totalPages}>Sledeća</button>
                </div>
                </>
            )}
        </div>
    );
}

export default MyOrders;
